import React, { useEffect, useState } from 'react';

function BookList() {
  const [books, setBooks] = useState([]);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [description, setDescription] = useState('');
  const [publishedDate, setPublishedDate] = useState('');

  // Component yüklendiğinde kitapları çek
  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      // API endpoint: http://localhost:5000/api/books 
      // veya https://localhost:5209/api/books
      const response = await fetch('https://localhost:5209/api/books'); 
      const data = await response.json();
      setBooks(data);
    } catch (error) {
      console.error("Kitapları çekerken hata oluştu", error);
    }
  };

  // Yeni kitap ekleme
  const addBook = async (e) => {
    e.preventDefault();

    const newBook = {
      title,
      author,
      description,
      publishedDate
    };

    try {
      const response = await fetch('https://localhost:5209/api/books', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newBook)
      });

      if (response.ok) {
        // Başarılı ekleme sonrası listeyi tekrar çek
        fetchBooks();

        // Formu temizle
        setTitle('');
        setAuthor('');
        setDescription('');
        setPublishedDate('');
      }
    } catch (error) {
      console.error("Kitap eklerken hata oluştu", error);
    }
  };

  // Kitap silme
  const deleteBook = async (id) => {
    try {
      const response = await fetch(`https://localhost:5209/api/books/${id}`, {
        method: 'DELETE'
      });
      if (response.ok) {
        // Silme sonrası listemizi güncelle
        fetchBooks();
      }
    } catch (error) {
      console.error("Kitap silerken hata oluştu", error);
    }
  };

  return (
    <div style={{ margin: '40px' }}>
      <h2>Book List</h2>
      <ul>
        {books.map((book) => (
          <li key={book.id}>
            <strong>{book.title}</strong> - {book.author} 
            <button onClick={() => deleteBook(book.id)}>Sil</button>
          </li>
        ))}
      </ul>

      <hr />

      <h3>Yeni Kitap Ekle</h3>
      <form onSubmit={addBook}>
        <div>
          <label>Title:</label>
          <input 
            type="text"
            value={title} 
            onChange={(e) => setTitle(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Author:</label>
          <input 
            type="text"
            value={author} 
            onChange={(e) => setAuthor(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Description:</label>
          <input 
            type="text"
            value={description} 
            onChange={(e) => setDescription(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Published Date:</label>
          <input 
            type="date"
            value={publishedDate} 
            onChange={(e) => setPublishedDate(e.target.value)} 
            required 
          />
        </div>
        <button type="submit">Ekle</button>
      </form>
    </div>
  );
}

export default BookList;
